// Process ALL inputs (all 31 RSS feeds)
const allItems = [];

// Loop through ALL inputs, not just the first one
for (const input of $input.all()) {
    const feedData = input.json;

    console.log('Processing feed:', feedData.rss?.channel?.title);

    // Extract items from this feed
    let rawItems;
    if (feedData.rss?.channel?.item) {
        rawItems = feedData.rss.channel.item;
    } else if (feedData.rss?.channel?.[0]?.item) {
        rawItems = feedData.rss.channel[0].item;
    } else {
        rawItems = [];
    }

    // Normalize to array
    const itemsArray = Array.isArray(rawItems) ? rawItems : [rawItems];

    console.log(`Feed has ${itemsArray.length} items`);

    // Add each item with feed metadata
    for (const item of itemsArray) {
        allItems.push({
            json: {
                ...item,
                feedCategory: feedData.category ?? '',
                feedSubcategory: feedData.subcategory ?? ''
            }
        });
    }
}

console.log(`Total items from all feeds: ${allItems.length}`);
return allItems;

const getText = val => Array.isArray(val) ? val[0] : val ?? '';

const getImageUrl = (entry) => {
  const mediaContent = entry['media:content'];
  if (mediaContent) {
    // Handle case where media:content is an array
    if (Array.isArray(mediaContent)) {
      const firstMedia = mediaContent[0];
      if (firstMedia?.url) return firstMedia.url;
      if (firstMedia?.$?.url) return firstMedia.$.url;
    }
    // Handle case where media:content is an object with url
    if (typeof mediaContent === 'object' && mediaContent.url) {
      return mediaContent.url;
    }
    // Handle case where media:content has $ object with url
    if (mediaContent.$?.url) return mediaContent.$.url;
  }
  // Fallback to other common image fields
  if (entry.enclosure?.url) return entry.enclosure.url;
  if (entry.image?.url) return entry.image.url;
  if (entry.thumbnail?.url) return entry.thumbnail.url;
  return null;
};

// Process ALL input items (each item from previous step)
return $input.all().map(input => {
  const entry = input.json;
  return {
    json: {
      title: getText(entry.title),
      article_url: getText(entry.link),
      image_url: getImageUrl(entry),
      pubDate: getText(entry.pubDate),
      description: getText(entry.description),
      categoryTag: getText(entry.category),
      feedCategory: entry.feedCategory || '',      // Preserve from previous step
      feedSubcategory: entry.feedSubcategory || '' // Preserve from previous step
    }
  };
});

function extractDomainFromUrl(url) {
    if (!url) return '';
    try {
        const withoutProtocol = url.replace(/^https?:\/\//, '');
        const domain = withoutProtocol.split('/')[0];
        return domain;
    } catch (error) {
        console.log('Domain extraction error:', error);
        return '';
    }
}

function extractCategory(url){
   if (!url) return '';
    try {
       const withoutProtocol = url.replace(/^https?:\/\//, '');
      const segments = withoutProtocol.split('/');
         if (segments.length > 1 && segments[1]) {
            return segments[1];
        }

        return '';
    } catch (error) {
        console.log('Category extraction error:', error);
        return '';
    }
}

function extractSourceFromUrl(url) {
    if (!url) return '';
    try {
        const withoutProtocol = url.replace(/^https?:\/\//, '');
        const domain = withoutProtocol.split('/')[0];
        const source = domain.replace(/^www\./, '').split('.')[0];
        return source;
    } catch (error) {
        console.log('Source extraction error:', error);
        return '';
    }
}

function generateId() {
    const timestamp = Date.now();
    const randomSuffix = Math.floor(Math.random() * 10000);
    const id = (timestamp * 10000 + randomSuffix) % 214748367
    return id;
}


function generateContentHash(title, description) {
    const content = (title || '') + (description || '');
    let hash = 0;
    for (let i = 0; i < content.length; i++) {
        const char = content.charCodeAt(i);
        hash = ((hash << 5) - hash) + char;
        hash = hash & hash;
    }
    return Math.abs(hash).toString(16);
}

function mapDataForDatabase(inputData, feedInfo) {
    return {
        id: generateId(),
        title: inputData.title || '',
        link: inputData.article_url || '',
        image: inputData.image_url || '',  
        description: inputData.description || '',
        source: extractSourceFromUrl(inputData.article_url),
        source_domain: extractDomainFromUrl(inputData.article_url),
        published_time: inputData.pubDate || '',
        category: inputData.categoryTag || extractCategory(inputData.article_url) || '',
        scraped_at: new Date().toISOString(),
        content_hash: generateContentHash(inputData.title, inputData.description),
        created_at: new Date().toISOString(),
        updated_at: new Date().toISOString()
    };
}

const outputItems = [];

for (const [index, inputItem] of $input.all().entries()) {
    const mappedData = mapDataForDatabase(inputItem.json, index);
    outputItems.push({
        json: mappedData
    });
}

return outputItems;
